<script>
export default {
  mounted() {
    const activateAnimation = document.querySelector("#activateAnimationColor");
    let checkBodyThemeMode = function () {
      let result = document.body.getAttribute("theme");
      return result;
    };
    checkBodyThemeMode();
    activateAnimation.addEventListener("click", () => {
      checkBodyThemeMode();
      if (
        checkBodyThemeMode() === "dark-colored" ||
        checkBodyThemeMode() === "light-colored"
      ) {
        // From COLOR to NON-COLOR
        const timeline = this.$anime
          .timeline()
          .add(
            {
              targets: ".circle-purple",
              easing: "spring(1, 80, 10, 0)",
              strokeWidth: "1.5",
              stroke: "#35424F",
              fill: "rgba(21, 26, 32, 1)",
              translateX: -5,
              translateY: -1,
            },
            400
          )
          .add(
            {
              targets: ".circle-yellow",
              easing: "spring(1, 80, 10, 0)",
              fill: "#ffffff",
              strokeWidth: "1.5",
              stroke: "#35424F",
              translateX: 0,
              translateY: 1,
            },
            400
          )
          .add(
            {
              targets: ".circle-red",
              easing: "spring(1, 80, 10, 0)",
              translateX: 5,
              translateY: 0,
              opacity: 0,
            },
            10
          )
          .add(
            {
              targets: ".superposition",
              opacity: 0,
            },
            50
          );
      } else {
        // From COLOR to NON-COLOR
        const timeline = this.$anime
          .timeline()
          .add(
            {
              targets: ".circle-purple",
              easing: "spring(1, 80, 10, 0)",
              strokeWidth: "0",
              stroke: "transparent",
              fill: "#615982",
              translateX: 0,
              translateY: 0,
            },
            400
          )
          .add(
            {
              targets: ".circle-yellow",
              easing: "spring(1, 80, 10, 0)",
              fill: "#F4D22A",
              strokeWidth: "0",
              stroke: "transparent",
              translateX: 0,
              translateY: 0,
            },
            400
          )
          .add(
            {
              targets: ".circle-red",
              easing: "spring(1, 80, 10, 0)",
              translateX: 0,
              translateY: 0,
              opacity: 1,
            },
            10
          )
          .add(
            {
              targets: ".superposition",
              easing: "easeInQuad",
              opacity: 1,
            },
            10
          );
      }
    });
  },
};
</script>

